/*Показать таланты*/
	function showElem(elem,type){
													// console.log(type)
	arr = elem.split('-')
													// console.log(arr)
													// console.log(arr[0])
		if(type == 't'){
				$('.talentstable').hide()
				$('#popup-talents').hide()
				$('#talents-'+arr[0]).show()
				$('#popup-talents').show()
				$('#page').hide()
		}
		if(type == 'c'){
				$('.talentstable').hide()
				$('#popup-talents').hide()
				$('#page').hide()
				$('#talents-'+arr[0]).show()
				$('#'+arr[0]+'-front').show()
				$('#popup-talents').show()
				
		}
	}

